mapper.select:
SELECT A.Corp_Code As "corpCode", A.Corp_Name As "corpName", A.Stock_Code As "stockCode", A.User_Id As "userId" , A.Create_Id As "createId", A.Create_Dt As "createDt", A.Change_Id As "changeId", A.Change_Dt As "changeDt" ,to_number((Select Bef_Cls_Price From Tb_Bplc_Detail Where Corp_Code = A.Corp_Code)) As "befClsPrice" , A.Memo As "memo" , B.Invest_Opinion As "investOpinion" , B.Opinion_Amount1 As "opinionAmount1" , B.Opinion_Amount2 As "opinionAmount2" , B.Opinion_Amount3 As "opinionAmount3" , B.Opinion_Amount4 As "opinionAmount4" , B.Opinion_Amount5 As "opinionAmount5" , B.Period_Gubn As "periodGubn", NVL((SELECT CODE_NM FROM TB_CODE WHERE CODE = B.Period_Gubn),'미정') As "periodNm" ,B.Estimate_Eps As "estimateEps", B.Estimate_Per As "estimatePer", B.Estimate_Cagr As "estimateCagr" FROM TB_INTEREST A, TB_INTEREST_DETAIL B WHERE 1=1 AND A.CORP_CODE = B.CORP_CODE(+) AND A.USER_ID = B.USER_ID(+) AND A.USER_ID = #{curUserId}
AND B.PERIOD_GUBN = #{pPeriodGubn}
AND B.PERIOD_GUBN is NULL

SELECT A.Corp_Code As "corpCode", A.Corp_Name As "corpName", A.Stock_Code As "stockCode", A.User_Id As "userId" , B.SEQ AS "seq", B.GUBN as "gubn", B.MEMO as "memo", B.CREATE_DT as "createDt", B.CHANGE_DT as "changeDt" FROM TB_INTEREST A, TB_INTEREST_MEMO B WHERE 1=1 AND A.CORP_CODE = B.CORP_CODE AND A.USER_ID = B.USER_ID AND A.USER_ID = #{curUserId}
AND A.CORP_CODE = #{pCorpCode}
ORDER BY B.CREATE_DT DESC

mapper.insert:


        (SELECT Max(seq)+1
			FROM tb_interest_memo where corp_code = #{pCorpCode} and user_id = #{curUserId} )




	INSERT INTO TB_INTEREST_MEMO (
		CORP_CODE, STOCK_CODE, USER_ID, SEQ 

			, MEMO 
		, CREATE_ID, CREATE_DT, CHANGE_ID, CHANGE_DT
	) VALUES (
		#{pCorpCode}
		, (SELECT STOCK_CODE FROM TB_BPLC_DETAIL WHERE CORP_CODE = #{pCorpCode})
		, #{curUserId}

			, #{seq}


			, 1


			, #{pMemo}
		, #{curUserId}, SYSDATE ,#{curUserId} ,TO_CHAR(SYSDATE,'YYYY-MM-DD')
	)


    DELETE TB_INTEREST_MEMO
	WHERE  1=1
	AND CORP_CODE = #{pCorpCode}
    AND USER_ID = #{curUserId}
	AND SEQ = #{pSeq}
